import React, { useState } from "react";
import { AiFillGithub } from "react-icons/ai";
import { BsBoxArrowUpRight } from "react-icons/bs";

const WorkCard = () => {
  const [isMyWorkActive, setisMyWorkActive] = useState(false);

  return (
    <div className="w-[500px] h-[630px] shadow-xl">
      {isMyWorkActive ? (
        <h1 className="text-4xl font-playFair py-3"> My Work </h1>
      ) : (
        ""
      )}{" "}
      {/* Project Screenshot  */}
      <div className="w-full h-[60%] bg-red-800"></div>
      {/* Project Info  */}
      <div className="w-full h-[40%] bg-slate-50">
        <div className="flex flex-col px-4 py-1 gap-2">
          <div className="flex justify-between items-center text-lg">
            <h3> Project Name</h3>
            <div className="flex gap-3">
              <AiFillGithub className="text-xl" />
              <BsBoxArrowUpRight />
            </div>
          </div>{" "}
          <p className="text-sm text-justify text-gray-700">
            {" "}
            Short description of the project. Just a couple sentences will do
          </p>
        </div>
      </div>
    </div>
  );
};

export default WorkCard;
